$doc = new DOMDocument();\nlibxml_disable_entity_loader(true);\n$doc->resolveExternals = false;\n$doc->substituteEntities = false;\n$doc->load('http://ws.audioscrobbler.com/2.0/artist/beatles/images.rss');\n\n$arrFeeds = array();\nforeach ($doc->getElementsByTagName('item') as $node) {\n    $mediaContent = $node->getElementsByTagName('media:content')->item(0);\n    $mediaThumbnail = $node->getElementsByTagName('media:thumbnail')->item(0);\n\n    $itemRSS = array(\n        'title' => $node->getElementsByTagName('title')->item(0)->nodeValue,\n        'desc' => $node->getElementsByTagName('description')->item(0)->nodeValue,\n        'link' => $node->getElementsByTagName('link')->item(0)->nodeValue,\n        'date' => $node->getElementsByTagName('pubDate')->item(0)->nodeValue,\n        'mediaContentUrl' => $mediaContent ? $mediaContent->getAttribute('url') : null,\n        'mediaThumbnailUrl' => $mediaThumbnail ? $mediaThumbnail->getAttribute('url') : null\n    );\n    array_push($arrFeeds, $itemRSS);\n}\n\n// Output the array for debugging purposes\nprint_r($arrFeeds);