$imageSrc = save_pic($PIC_URL, $pk);\n\nfunction save_pic($pic_url, $pk) {\n    $imageDir = './';\n    if (!strlen($pic_url)) {\n        return "removed.jpg";\n    }\n    if (!is_dir($imageDir) || !is_writable($imageDir)) {\n        return "removed.jpg";\n    }\n    // Suppress warnings using @ and check for false\n    $image = @file_get_contents($pic_url);\n    if ($image === false) {\n        return "removed.jpg";\n    }\n    $r = file_put_contents($imageDir.$pk.".jpg", $image);\n    if ($r !== false) {\n        return "./$pk.jpg"; \n    } else {\n        return "removed.jpg";\n    }\n}