<?php\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Fetch items collected by a specific user\n$user_id = 1;\n$stmt = $mysqli->prepare("SELECT items.item_name, items.item_hash_value FROM items \n                          JOIN user_items ON items.item_id = user_items.item_id \n                          WHERE user_items.user_id = ?");\n$stmt->bind_param("i", $user_id);\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo "Item Name: " . $row["item_name"] . " - Hash: " . $row["item_hash_value"] . "<br>";\n}\n\n$stmt->close();\n$mysqli->close();\n?>