<?php\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n$item_name = "example_item";\n$item_hash_value = generateHash($item_name);\n\n// Insert item\n$stmt = $mysqli->prepare("INSERT INTO items (item_name, item_hash_value) VALUES (?, ?)");\n$stmt->bind_param("ss", $item_name, $item_hash_value);\n$stmt->execute();\n\n// Get last inserted item_id\n$item_id = $stmt->insert_id;\n\n// Assuming you already have a user_id\n$user_id = 1;\n\n// Associate item with user\n$stmt = $mysqli->prepare("INSERT INTO user_items (user_id, item_id) VALUES (?, ?)");\n$stmt->bind_param("ii", $user_id, $item_id);\n$stmt->execute();\n\n$stmt->close();\n$mysqli->close();\n?>