class MyForm extends Zend_Form\n{\n    public function init()\n    {\n        // Loop to create multiple text elements\n        for ($i = 1; $i <= 5; $i++) { // Adjust the range as needed\n            $this->addElement('text', "element[$i]", array(\n                'label'    => "Element $i",\n                'required' => true,\n                'filters'  => array('StringTrim'),\n                'validators' => array(\n                    array('validator' => 'StringLength', 'options' => array(0, 100))\n                )\n            ));\n        }\n    }\n}