<?php\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "zipcode_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$zip = $_POST['zipcode'];\n\nif (preg_match('/^\d{5}$/', $zip)) {\n    $stmt = $conn->prepare("SELECT * FROM zipcodes WHERE zipcode = ?");\n    $stmt->bind_param("s", $zip);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    if ($result->num_rows > 0) {\n        while ($row = $result->fetch_assoc()) {\n            echo "Zip Code: " . $row["zipcode"] . " - Location: " . $row["location"];\n        }\n    } else {\n        echo "No results found";\n    }\n    $stmt->close();\n} else {\n    echo "Invalid Zip Code";\n}\n\n$conn->close();\n?>