// app/Http/Middleware/MyCustomMiddleware.php\nnamespace App\Http\Middleware;\n\nuse Closure;\n\nclass MyCustomMiddleware\n{\n    public function handle($request, Closure $next)\n    {\n        // Perform actions before the request reaches the controller\n        \Log::info('Before processing the request');\n\n        $response = $next($request);\n\n        // Perform actions after the response leaves the controller\n        \Log::info('After processing the request');\n\n        return $response;\n    }\n}\n\n// Register the middleware in app/Http/Kernel.php\nprotected $middleware = [\n    \App\Http\Middleware\MyCustomMiddleware::class,\n];