<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Replace `item_id_value` with the actual item ID you are calculating the weighted mean for\n$item_id = 'item_id_value';\n\n// Query to get ratings and weights for the specified item\n$sql = "SELECT rating, weight FROM ratings WHERE item_id = ?";\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("i", $item_id);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$weighted_sum = 0;\n$total_weight = 0;\n\n// Calculate weighted sum and total weight\nwhile ($row = $result->fetch_assoc()) {\n    $weighted_sum += $row['rating'] * $row['weight'];\n    $total_weight += $row['weight'];\n}\n\n// Calculate weighted mean\n$weighted_mean = ($total_weight > 0) ? ($weighted_sum / total_weight) : 0;\n\necho "Weighted Mean: " . round($weighted_mean, 2);\n\n$stmt->close();\n$conn->close();\n?>