<?php\nclass Person {\n    public $name;\n    public $age;\n\n    // Constructor\n    public function __construct($name, $age) {\n        $this->name = $name;\n        $this->age = $age;\n    }\n\n    public function introduce() {\n        echo "Hi, my name is " . $this->name . " and I am " . $this->age . " years old.";\n    }\n}\n\n// Creating an instance of the Person class\n$person = new Person("John", 30);\n\n// Calling a method\n$person->introduce();\n?>