<?php\nfunction generateMetaTags($content, $numKeywords = 10) {\n    // Common stop words to exclude from keywords\n    $stopWords = array('i', 'me', 'my', 'myself', 'we', 'our', 'ours', 'ourselves', 'you', 'your', 'yours', 'yourself', 'yourselves', 'he', 'him', 'his', 'himself', 'she', 'her', 'hers', 'herself', 'it', 'its', 'itself', 'they', 'them', 'their', 'theirs', 'themselves', 'what', 'which', 'who', 'whom', 'this', 'that', 'these', 'those', 'am', 'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'having', 'do', 'does', 'did', 'doing', 'a', 'an', 'the', 'and', 'but', 'if', 'or', 'because', 'as', 'until', 'while', 'of', 'at', 'by', 'for', 'with', 'about', 'against', 'between', 'into', 'through', 'during', 'before', 'after', 'above', 'below', 'to', 'from', 'up', 'down', 'in', 'out', 'on', 'off', 'over', 'under', 'again', 'further', 'then', 'once', 'here', 'there', 'when', 'where', 'why', 'how', 'all', 'any', 'both', 'each', 'few', 'more', 'most', 'other', 'some', 'such', 'no', 'nor', 'not', 'only', 'own', 'same', 'so', 'than', 'too', 'very', 's', 't', 'can', 'will', 'just', 'don', 'should', 'now');\n\n    // Extract description - first 150 characters or first sentence\n    $description = substr($content, 0, 150);\n\n    // Generate keywords\n    $words = str_word_count(strtolower(strip_tags($content)), 1);\n    $words = array_diff($words, $stopWords);\n    $wordFrequency = array_count_values($words);\n    arsort($wordFrequency);\n    $keywords = array_slice(array_keys($wordFrequency), 0, $numKeywords);\n\n    // Generate META tags\n    $metaDescription = '<meta name="description" content="' . htmlspecialchars($description) . '">';\n    $metaKeywords = '<meta name="keywords" content="' . htmlspecialchars(implode(', ', $keywords)) . '">';\n\n    return $metaDescription . "\n" . $metaKeywords;\n}\n\n// Example content\n$content = "Your CMS content goes here. This is the first sentence. This is the second sentence. And so on...";\n\n// Generate META tags\necho generateMetaTags($content);\n?>