<?php\n   // Define the function to be executed\n   function itstime() {\n       // Your code to write information to a log file and send an email\n       $logFile = 'log.txt';\n       $message = "It's time! " . date('Y-m-d H:i:s') . "\n";\n       file_put_contents($logFile, $message, FILE_APPEND);\n       \n       // Check your condition and send email if needed\n       // Example condition\n       if (true) { // Replace this with your actual condition\n           mail('example@example.com', 'Hourly Update', $message);\n       }\n   }\n\n   // Infinite loop to run the function every hour\n   while (true) {\n       itstime();\n       // Wait for 1 hour (3600 seconds)\n       sleep(3600);\n   }