<?php\n// form_handler.php\n\nfunction processForm($formName) {\n    switch ($formName) {\n        case 'profile':\n            include 'forms/profile.form.php';\n            break;\n        // Add cases for other forms as needed\n        default:\n            throw new Exception('Unknown form name');\n    }\n\n    // Process the form if a POST request is detected\n    if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])) {\n        // Assuming each form file defines a processForm function\n        processForm();\n    }\n}\n?>