<?php\ndeclare(strict_types=1);\n\nerror_reporting(E_ALL | E_STRICT);\n\nclass Joe {\n    private $lastName;\n\n    public function __set($name, $value) {\n        throw new Exception("Attempting to set undefined property '$name'");\n    }\n\n    public function __get($name) {\n        throw new Exception("Attempting to get undefined property '$name'");\n    }\n}\n\n$joe = new Joe();\ntry {\n    $joe->lastNombre = "Smith"; // This will throw an exception\n} catch (Exception $e) {\n    echo $e->getMessage(), "\n";\n}\n\ntry {\n    echo "And here it is " . $jose; // This will throw a notice\n} catch (Exception $e) {\n    echo $e->getMessage(), "\n";\n}\n?>