<?php\n\nfunction priceDisplay($amount, $currencyRate) {\n    return round($amount * $currencyRate, 2);\n}\n\nfunction calculateCartTotals($cartItems, $shippingCost, $currencyRate) {\n    $subtotal = 0.0;\n    foreach ($cartItems as $item) {\n        $subtotal += $item['quantity'] * $item['price'];\n    }\n\n    $total = $subtotal + $shippingCost;\n\n    // Convert amounts to target currency\n    $subtotalConverted = priceDisplay($subtotal, $currencyRate);\n    $shippingCostConverted = priceDisplay($shippingCost, $currencyRate);\n    $totalConverted = priceDisplay($total, $currencyRate);\n\n    return [\n        'subtotal' => $subtotal,\n        'subtotal_converted' => $subtotalConverted,\n        'shipping_cost' => $shippingCost,\n        'shipping_cost_converted' => $shippingCostConverted,\n        'total' => $total,\n        'total_converted' => $totalConverted,\n    ];\n}\n\n// Example usage\n$cartItems = [\n    ['price' => 9.75, 'quantity' => 1],\n    // Add more items as needed\n];\n\n$shippingCost = 5.95;\n$currencyRate = 1.1;\n\n$totals = calculateCartTotals($cartItems, $shippingCost, $currencyRate);\n\necho "Subtotal: {$totals['subtotal_converted']} \n";\necho "Shipping Cost: {$totals['shipping_cost_converted']} \n";\necho "Total: {$totals['total_converted']} \n";\n?>