<?php\n// Load the image\n$imagePath = 'path_to_your_image.gif';\n$image = imagecreatefromgif($imagePath);\n\n// Define the colors you want to swap\n$white = imagecolorallocate($image, 255, 255, 255);\n$black = imagecolorallocate($image, 0, 0, 0);\n$blue = imagecolorallocate($image, 173, 216, 230); // light blue\n\n// Get the image dimensions\n$width = imagesx($image);\n$height = imagesy($image);\n\n// Loop through each pixel and swap colors\nfor ($x = 0; $x < $width; $x++) {\n    for ($y = 0; $y < $height; $y++) {\n        $currentColor = imagecolorat($image, $x, $y);\n        \n        // Swap white with black\n        if ($currentColor == $white) {\n            imagesetpixel($image, $x, $y, $black);\n        }\n        // Swap specific color with light blue (example for graph line)\n        elseif ($currentColor == imagecolorallocate($image, 255, 0, 0)) { // assuming the graph line is red\n            imagesetpixel($image, $x, $y, $blue);\n        }\n    }\n}\n\n// Output and save the modified image\nheader('Content-Type: image/gif');\nimagegif($image);\nimagedestroy($image);\n?>