// Load phpBB3's common.php file\ndefine('IN_PHPBB', true);\n$phpbb_root_path = '/path/to/phpbb/';\n$phpEx = substr(strrchr(__FILE__, '.'), 1);\ninclude($phpbb_root_path . 'common.' . $phpEx);\n\n// Initialize phpBB3 session\n$user->session_begin();\n$auth->acl($user->data);\n$user->setup();\n\n// Example function to check if a user is logged in\nfunction is_logged_in() {\n    global $user;\n    return $user->data['user_id'] != ANONYMOUS;\n}\n\n// Example usage in a CodeIgniter controller\nclass Welcome extends CI_Controller {\n    public function index() {\n        if (is_logged_in()) {\n            $data['username'] = $GLOBALS['user']->data['username'];\n            $this->load->view('welcome_message', $data);\n        } else {\n            redirect('login');\n        }\n    }\n}