class Object_Model {\n    public static function load($id) {\n        // Instantiate the object\n        $object = new static($id);\n        return $object;\n    }\n\n    public static function loadMultiple($ids) {\n        $objects = [];\n        foreach ($ids as $id) {\n            $objects[] = new static($id);\n        }\n        return $objects;\n    }\n}\n\nclass Object1 extends Object_Model {\n    // Additional methods and properties specific to Object1\n}\n\nclass Object2 extends Object_Model {\n    // Additional methods and properties specific to Object2\n}