<?php\nheader("Content-type: image/png");\n\n// example usage: <img src="gradient.php?height=100&width=600&start=00FF00&end=ff0000" />\n$height = 100;\n$width = 600;\n$start = '000000';\n$end = 'FFFFFF';\nextract($_REQUEST); // overwrite using vars from URL\n\n$start_r = hexdec(substr($start, 0, 2));\n$start_g = hexdec(substr($start, 2, 2));\n$start_b = hexdec(substr($start, 4, 2));\n$end_r = hexdec(substr($end, 0, 2));\n$end_g = hexdec(substr($end, 2, 2));\n$end_b = hexdec(substr($end, 4, 2));\n\n$image = imagecreatetruecolor($width, $height);\n\nfor ($x = 0; $x < $width; $x++) {\n    $new_r = $start_r + intval((($end_r - $start_r) / $width) * $x);\n    $new_g = $start_g + intval((($end_g - $start_g) / $width) * $x);\n    $new_b = $start_b + intval((($end_b - $start_b) / $width) * $x);\n\n    $color = imagecolorallocate($image, $new_r, $new_g, $new_b);\n    imageline($image, $x, 0, $x, $height, $color);\n}\n\nimagepng($image);\nimagedestroy($image);\n?>