// Apply htmlentities to all GET parameters\nforeach ($_GET as $key => $value) {\n    $_GET[$key] = htmlentities($value, ENT_QUOTES, 'UTF-8');\n}\n\n// Apply htmlentities to all POST parameters\nforeach ($_POST as $key => $value) {\n    $_POST[$key] = htmlentities($value, ENT_QUOTES, 'UTF-8');\n}\n\n// Apply htmlentities to all COOKIE parameters\nforeach ($_COOKIE as $key => $value) {\n    $_COOKIE[$key] = htmlentities($value, ENT_QUOTES, 'UTF-8');\n}