class MyModel extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'my_table'; // Replace with your actual table name\n\n    public function __call($method, $arguments)\n    {\n        if (strpos($method, 'fetchBy') === 0) {\n            // Extract the column name from the method name\n            $column = strtolower(substr($method, 7));\n            $value = $arguments[0];\n            return $this->fetchAll("$column = ?", $value)->current();\n        }\n        \n        if (strpos($method, 'findBy') === 0) {\n            // Extract the column name from the method name\n            $column = strtolower(substr($method, 6));\n            $value = $arguments[0];\n            return $this->fetchAll("$column = ?", $value)->current();\n        }\n\n        // If method does not match our magic method, call parent __call\n        return parent::__call($method, $arguments);\n    }\n}