try {\n    $conn = new PDO("sqlsrv:server=your_server_name;Database=your_database_name", "your_username", "your_password");\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $sql = "\n    DECLARE @id bigint;\n    SET @id = (SELECT MAX(application_track_id) + 1 FROM application_track_data);\n    INSERT INTO application_track_data (application_track_id, user_id, action_key, action, ip_address, session_id, application) \n    VALUES (@id, 1, '584', 'login', '192.168.37.60', '05sn3618p61dvmml6pkefuteg2', 'akamata');\n    ";\n\n    $stmt = $conn->prepare($sql);\n    $stmt->execute();\n\n    echo "Success";\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n\n// Close the connection\n$conn = null;