class Hello {\n    private static $greeting = 'Hello';\n\n    // This method will act as a static initializer\n    private static function initialize() {\n        if (self::$greeting === 'Hello') {\n            self::$greeting .= ' There!';\n        }\n    }\n\n    public static function greet() {\n        self::initialize();\n        echo self::$greeting;\n    }\n}\n\n// Usage\nHello::greet(); // Outputs: Hello There!