function combineArrays($arrays, $i = 0) {\n    if (!isset($arrays[$i])) {\n        return array(array());\n    }\n\n    $result = array();\n    $subComb = combineArrays($arrays, $i + 1);\n\n    foreach ($arrays[$i] as $value) {\n        foreach ($subComb as $combination) {\n            $result[] = array_merge(array($value), $combination);\n        }\n    }\n\n    return $result;\n}\n\n$as = array($optionA1, $optionA2);\n$bs = array($optionB1, $optionB2);\n$cs = array($optionC1, $optionC2);\n\n$allCombinations = combineArrays(array($as, $bs, $cs));\n\nforeach ($allCombinations as $combination) {\n    $result = $this->method($combination[0], $combination[1], $combination[2]);\n    if ($result) {\n        // handle the result\n    }\n}