<?php\nfunction getExtensionsByMimeType($mimeType) {\n    $mimeMap = [\n        'image/jpeg' => ['jpg', 'jpeg'],\n        'image/png' => ['png'],\n        'image/gif' => ['gif'],\n        'application/pdf' => ['pdf'],\n        'text/plain' => ['txt'],\n        'text/html' => ['html', 'htm'],\n        'application/zip' => ['zip'],\n        'application/msword' => ['doc'],\n        'application/vnd.openxmlformats-officedocument.wordprocessingml.document' => ['docx'],\n        // Add more MIME types and their extensions as needed\n    ];\n\n    return $mimeMap[$mimeType] ?? [];\n}\n\n// Example usage:\n$mimeType = 'image/jpeg';\n$extensions = getExtensionsByMimeType($mimeType);\n\nprint_r($extensions);\n?>