$questionId = 57;\n$id = "zee";\n\n$mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("\n    SELECT Vote1a, QuestionId, MemberId\n    FROM qotwVote1a\n    WHERE QuestionId = ?\n    AND MemberId = ?\n");\n$stmt->bind_param("is", $questionId, $id);\n$stmt->execute();\n$result2 = $stmt->get_result();\n\n$j = 0;\n\nif ($result2 && $result2->num_rows > 0) {\n    $j = 1;\n    while ($row2 = $result2->fetch_assoc()) {\n        echo $row2['Vote1a'] . " " . $row2['QuestionId'] . " " . $row2['MemberId'];\n        echo "<br/>";\n    }\n}\n\necho($j);\n\n$stmt->close();\n$mysqli->close();