class A {\n\n    static public function getStatic() {\n        return static::$myStatic;\n    }\n\n}\n\nclass B extends A {\n\n    public static $myStatic = 5;\n\n}\n\nclass C extends A {\n\n    public static $myStatic = 6;\n\n}\n\nvar_dump(B::$myStatic); // Outputs: int(5)\nvar_dump(B::getStatic()); // Outputs: int(5)\n\nvar_dump(C::$myStatic); // Outputs: int(6)\nvar_dump(C::getStatic()); // Outputs: int(6)