<?php\n// Assuming you're using PDO for database connection\ntry {\n    $pdo = new PDO('pgsql:host=your_host;dbname=your_dbname', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $query = "UPDATE A SET type = :type WHERE id = :id";\n    $stmt = $pdo->prepare($query);\n    $stmt->bindParam(':type', $strType, PDO::PARAM_STR);\n    $stmt->bindParam(':id', intval($ID), PDO::PARAM_INT);\n\n    $stmt->execute();\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>