<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n$keyword = $_GET['keyword']; // Get the keyword from a request\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare and bind\n$stmt = $conn->prepare("SELECT id, content, LOCATE(?, content) AS position FROM your_table WHERE content LIKE CONCAT('%', ?, '%')");\n$stmt->bind_param("ss", $keyword, $keyword);\n\n// Execute\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Display results\nwhile ($row = $result->fetch_assoc()) {\n    $content = $row['content'];\n    $position = $row['position'];\n\n    // Calculate the start and end positions for summary\n    $start = max(0, $position - 100);\n    $end = min(strlen($content), $position + strlen($keyword) + 100);\n\n    // Extract the summary\n    $summary = substr($content, $start, $end - $start);\n\n    // Highlight the keyword\n    $summary = str_replace($keyword, "<strong>$keyword</strong>", $summary);\n\n    echo "<p>Summary: " . htmlspecialchars($summary) . "</p>";\n}\n\n// Close connections\n$stmt->close();\n$conn->close();\n?>