<?php\nfunction decrypt($key1, $key2, $key3, $iv, $token) {\n    // Concatenate the keys\n    $key = $key1 . $key2 . $key3;\n\n    // Convert the token from hexadecimal to binary\n    $ciphertext = hex2bin($token);\n\n    // Decrypt the ciphertext using 3DES in CBC mode\n    $decrypted_string = openssl_decrypt($ciphertext, 'des-ede3-cbc', $key, OPENSSL_RAW_DATA, $iv);\n\n    return $decrypted_string;\n}\n\n// Three 16 character long keys\n$key1 = '1234567890123456';\n$key2 = '6543210987654321';\n$key3 = '6549873210456123';\n\n// The initialization vector\n$iv = '0102030405060708';\n\n// A token which looks something like this\n$token = '2ad82e09e3b4e7c491e4bb9fb7c606e1';\n\n// Decrypt the token\n$decrypted_string = decrypt($key1, $key2, $key3, $iv, $token);\n\necho "Decrypted string: " . $decrypted_string;\n?>