session_start();\n\n// Assuming $user_id is the ID of the logged-in user\nfunction loadBookmarksIntoSession($user_id) {\n    // Fetch bookmarked item IDs from the database\n    $bookmarkedItems = fetchBookmarkedItemsFromDB($user_id);\n    \n    // Store the bookmarked item IDs in the session\n    $_SESSION['bookmarked_items'] = $bookmarkedItems;\n}\n\nfunction fetchBookmarkedItemsFromDB($user_id) {\n    // Example database query (using PDO for security)\n    $pdo = new PDO('mysql:host=hostname;dbname=database', 'username', 'password');\n    $stmt = $pdo->prepare("SELECT item_id FROM bookmarks WHERE user_id = :user_id");\n    $stmt->execute(['user_id' => $user_id]);\n    return $stmt->fetchAll(PDO::FETCH_COLUMN);\n}\n\nfunction isBookmarked($item_id) {\n    // Check if the item ID is in the session array\n    return in_array($item_id, $_SESSION['bookmarked_items']);\n}\n\n// Example usage\n$user_id = 1; // Replace with the actual user ID\nloadBookmarksIntoSession($user_id);\n\n$item_id = 123; // Replace with the actual item ID\nif (isBookmarked($item_id)) {\n    echo "Item is bookmarked.";\n} else {\n    echo "Item is not bookmarked.";\n}