<?php\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_dbname";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$search_term = 'milwakee'; // Example search term\n$sql = "SELECT city, levenshtein(city, ?) AS distance FROM cities ORDER BY distance LIMIT 5";\n\n$stmt = $conn->prepare($sql);\n$stmt->bind_param("s", $search_term);\n$stmt->execute();\n$stmt->bind_result($city, $distance);\n\n$results = [];\nwhile ($stmt->fetch()) {\n    $results[] = ['city' => $city, 'distance' => $distance];\n}\n\n$stmt->close();\n$conn->close();\n\nusort($results, function($a, $b) {\n    return $a['distance'] - $b['distance'];\n});\n\nprint_r($results);\n?>