<?php\n$allowed_pages = ['home', 'about', 'contact']; // Define a whitelist of allowed pages\n$page = isset($_GET['page']) ? $_GET['page'] : 'home'; // Default page\n\nif (in_array($page, $allowed_pages)) {\n    $file_path = sprintf("pages/%s.php", $page);\n    if (file_exists($file_path)) {\n        include $file_path;\n    } else {\n        echo "The page '$page' does not exist =(";\n    }\n} else {\n    echo "Invalid page!";\n}\n?>