$userId = 123; // Replace with the current user's ID\n\n$query = "\nSELECT \n    fb.auto_id, \n    fb.user_id, \n    fb.bulletin, \n    fb.subject, \n    fb.color, \n    fb.submit_date, \n    fru.disp_name, \n    fru.pic_url\nFROM \n    friend_bulletin fb\nJOIN \n    friend_friend ff ON (fb.user_id = ff.userid OR fb.user_id = ff.friendid)\nJOIN \n    friend_reg_user fru ON fb.user_id = fru.auto_id\nWHERE \n    (ff.userid = ? OR ff.friendid = ?)\n    AND fb.status = 'Active'\n    AND fb.spam = '0'\n    AND ff.status = '1'\nORDER BY \n    fb.submit_date DESC\n";\n\n// Prepare and execute the query using a safe method to prevent SQL injection\n$stmt = $pdo->prepare($query);\n$stmt->execute([$userId, $userId]);\n$bulletins = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Display bulletins\nforeach ($bulletins as $bulletin) {\n    echo '<div class="bulletin">';\n    echo '<img src="' . htmlspecialchars($bulletin['pic_url']) . '" alt="' . htmlspecialchars($bulletin['disp_name']) . '">';\n    echo '<h2>' . htmlspecialchars($bulletin['subject']) . '</h2>';\n    echo '<p>' . htmlspecialchars($bulletin['bulletin']) . '</p>';\n    echo '<span>' . htmlspecialchars($bulletin['submit_date']) . '</span>';\n    echo '</div>';\n}