function checkProxyUsingIPHub($ip) {\n    $apiKey = 'YOUR_API_KEY';\n    $url = "http://v2.api.iphub.info/ip/{$ip}";\n    \n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch, CURLOPT_HTTPHEADER, [\n        "X-Key: {$apiKey}"\n    ]);\n    \n    $response = curl_exec($ch);\n    curl_close($ch);\n    \n    if ($response === false) {\n        return false; // Could not fetch data\n    }\n    \n    $result = json_decode($response, true);\n    \n    return isset($result['block']) && $result['block'] == 1;\n}\n\n$user_ip = $_SERVER['REMOTE_ADDR'];\nif (checkProxyUsingIPHub($user_ip)) {\n    echo "Proxy detected";\n} else {\n    echo "No proxy detected";\n}