<?php\n\n$Valid = null; // Initialize $Valid to null globally\n\nfunction Validate(){\n    if(!empty($_POST["From"]) && !empty($_POST["Body"])){\n        return true;\n    } else {\n        return false;\n    }\n}\n\nfunction Postback(){\n    if(isset($_POST["Return"])){\n        return true;\n    } else {\n        return false;\n    }\n}\n\nfunction Email(){\n    global $Valid; // Access the global $Valid variable\n\n    if(isset($Valid)){\n        if($Valid != 0){\n            return true;\n        } else {\n            return false;\n        }\n    } else {\n        if(Postback()){\n            if(Validate()){\n                $Valid = 1;\n                // Send mail logic here if needed\n            } else {\n                $Valid = 0;\n            }\n        }\n    }\n    return null; // Return null if none of the conditions are met\n}\n\nEmail();