require 'path/to/PHPMailerAutoload.php'; // Adjust the path as necessary\n\n$mail = new PHPMailer();\n\n// Adding the body\n$body = file_get_contents('template/index.htm');\n\n// Set the email subject\n$mail->Subject = "PHPMailer Test Subject via mail(), basic";\n\n// Set an alternative plain text body\n$mail->AltBody = "To view this message, please use an HTML compatible email viewer!";\n\n// Set the sender's address\n$mail->SetFrom('xxxxxxx@example.com', 'Your Name');\n\n// Add recipient's address\n$address = "recipient@example.com";\n$mail->AddAddress($address, "Recipient Name");\n\n// Embed images and replace the placeholders in the HTML\n$mail->AddEmbeddedImage('template/images/bullet_point.gif', 'bullet_point');\n$mail->AddEmbeddedImage('template/images/template_1_01.gif', 'template_1_01');\n$mail->AddEmbeddedImage('template/images/template_1_03.gif', 'template_1_03');\n\n$body = str_replace(\n    ['src="template/images/bullet_point.gif"', 'src="template/images/template_1_01.gif"', 'src="template/images/template_1_03.gif"'],\n    ['src="cid:bullet_point"', 'src="cid:template_1_01"', 'src="cid:template_1_03"'],\n    $body\n);\n\n// Set the HTML body\n$mail->MsgHTML($body);\n\nif (!$mail->Send()) {\n    echo "Mailer Error: " . $mail->ErrorInfo;\n} else {\n    echo "Message sent!";\n}