<?php\nfunction smarty_function_recursive_list($params, $smarty) {\n    $entries = $params['entries'];\n    if (!empty($entries)) {\n        echo '<ul>';\n        foreach ($entries as $entry) {\n            echo '<li>';\n            echo '<h1>' . htmlspecialchars($entry['headline'], ENT_QUOTES, 'UTF-8') . '</h1>';\n            echo '<p>' . htmlspecialchars($entry['body'], ENT_QUOTES, 'UTF-8') . '</p>';\n            if (!empty($entry['children'])) {\n                smarty_function_recursive_list(['entries' => $entry['children']], $smarty);\n            }\n            echo '</li>';\n        }\n        echo '</ul>';\n    }\n}\n?>