<?php\n// Fetch event data from MySQL database\n// Assuming you have a PDO connection $pdo\n$events = array();\ntry {\n    $stmt = $pdo->query("SELECT id, title, start, end FROM events");\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $events[] = array(\n            'id' => $row['id'],\n            'title' => $row['title'],\n            'start' => $row['start'],\n            'end' => isset($row['end']) ? $row['end'] : null\n        );\n    }\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n\n// Encode the array to JSON\necho json_encode($events);\n?>