<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Function to delete post and related comments\nfunction deletePost($postID) {\n    global $conn;\n\n    // Start transaction\n    $conn->begin_transaction();\n\n    try {\n        // Delete comments related to the post\n        $stmt = $conn->prepare("DELETE FROM Comments WHERE PostID = ?");\n        $stmt->bind_param("i", $postID);\n        $stmt->execute();\n        $stmt->close();\n\n        // Delete the post\n        $stmt = $conn->prepare("DELETE FROM Posts WHERE PostID = ?");\n        $stmt->bind_param("i", $postID);\n        $stmt->execute();\n        $stmt->close();\n\n        // Commit transaction\n        $conn->commit();\n        echo "Post and related comments deleted successfully.";\n    } catch (Exception $e) {\n        // Rollback transaction on error\n        $conn->rollback();\n        echo "Error deleting post and related comments: " . $e->getMessage();\n    }\n}\n\n// Example usage\n$postID = 123; // Replace with the actual PostID you want to delete\ndeletePost($postID);\n\n$conn->close();\n?>