class Database {\n    // Ensure this escape function is properly implemented\n    public static function escape($value) {\n        // Use the appropriate escaping mechanism for your database\n        return mysqli_real_escape_string($your_mysqli_connection, $value);\n    }\n\n    public static function prepare($var) {\n        if (is_null($var)) {\n            return 'NULL';\n        } else if (is_object($var) || is_array($var)) {\n            return "'" . self::escape(serialize($var)) . "'";\n        } else if (is_bool($var)) {\n            return (int)$var;\n        } else if (is_int($var)) {\n            return $var;\n        } else if (is_float($var)) {\n            // Ensure proper formatting of floats\n            return "'" . self::escape((string)$var) . "'";\n        } else if (is_string($var)) {\n            return "'" . self::escape($var) . "'";\n        } else {\n            throw new Exception('Unsupported variable type [' . gettype($var) . ']');\n        }\n    }\n}