class PersonEvent {\n    protected $person;\n    protected $event;\n\n    public function __construct(PersonInterface $person, EventInterface $event) {\n        $this->person = $person;\n        $this->event = $event;\n    }\n\n    public function getPersonDetails() {\n        return $this->person->getPersonDetails();\n    }\n\n    public function getEventType() {\n        return $this->event->getEventType();\n    }\n}