class DomainObjectFactory {\n    public static function create($data) {\n        if ($data['type'] == 'person_event') {\n            $person = new Person($data['person_details']);\n            $event = new Event($data['event_type']);\n            return new PersonEvent($person, $event);\n        }\n        // Add other conditions for different types\n\n        // Default case\n        throw new Exception("Unknown type");\n    }\n}