<?php\n$is_true = true; // Replace this with your condition\n\nif ($is_true) {\n    echo <<<HTML\n    <div id="login">\n      <h2>Login</h2>\n      <div class="box">\n        <form method="POST" action="index.php/login">\n          Username/Email:<br />\n          <input type="text" name="username" value="<?php echo set_value("username"); ?>" size="50" class="form" />\n          <?php echo form_error("username"); ?>\n          Password:<br />\n          <input type="password" name="password" value="<?php echo set_value("password"); ?>" size="50" class="form" />\n          <?php echo form_error("password"); ?>\n          <input type="submit" value="Login" name="login" />\n        </form>\n      </div>\n    </div>\nHTML;\n} else {\n    // Your false condition HTML goes here\n    echo <<<HTML\n    <div id="other">\n      <h2>Other Content</h2>\n      <div class="box">\n        <!-- Add your alternative HTML content here -->\n      </div>\n    </div>\nHTML;\n}\n?>