<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// To get the structure of a given table\n$tableName = "your_table_name";\n$sqlDescribe = "DESCRIBE $tableName";\n$resultDescribe = $conn->query($sqlDescribe);\n\nif ($resultDescribe->num_rows > 0) {\n    while($row = $resultDescribe->fetch_assoc()) {\n        print_r($row);\n    }\n} else {\n    echo "No structure found for the table.";\n}\n\n// To get a list of all tables\n$sqlShowTables = "SHOW TABLES";\n$resultShowTables = $conn->query($sqlShowTables);\n\nif ($resultShowTables->num_rows > 0) {\n    while($row = $resultShowTables->fetch_assoc()) {\n        echo $row["Tables_in_$dbname"] . "\n";\n    }\n} else {\n    echo "No tables found in the database.";\n}\n\n$conn->close();\n?>