class Controller {\n    public $Model;\n\n    function __construct() {\n        // Assuming parent constructor does something necessary\n        parent::__construct();\n\n        $this->loadModel("AccountModel", "Model");\n        \n        $account = $this->getUserAccount();\n        // ... Other initialization code\n    }\n\n    function loadModel($model_name, $var_name) {\n        // Assuming $class_name is derived from $model_name correctly\n        $class_name = $model_name; \n        $obj = new $class_name();\n        $this->$var_name = $obj;\n    }\n\n    function getUserAccount() {\n        // Assuming $_SESSION["account"]["user_account_id"] is set correctly\n        $user_account_id = $_SESSION["account"]["user_account_id"];\n        return $this->Model->getAccount($user_account_id);\n    }\n}\n\nclass AccountModel {\n    public function getAccount($user_account_id) {\n        // Your logic to get account details\n        return "Account details for user id: $user_account_id";\n    }\n}\n\n// Example usage:\n$controller = new Controller();