// Perform security checks, get the filename from the request, prefix document download directory, and check for file existence\n\n// Set headers to force download and specify the filename\nheader('Content-Type: application/pdf');\nheader('Content-Disposition: inline; filename="' . basename($file) . '"');\nheader('Content-Transfer-Encoding: binary');\nheader('Content-Length: ' . filesize($file));\nheader('Connection: close');\n\n// Read the file and deliver it to the user\nset_time_limit(0);\nreadfile($file);