public function __construct()\n{\n    parent::__construct();\n\n    $this->load->library('session');\n    $this->load->helper('cookie');\n\n    if (!$this->session->userdata('logged_in')) {\n        $remember_me = $this->input->cookie('remember_me', TRUE);\n        if ($remember_me) {\n            $username = base64_decode($remember_me);\n            // Optionally, validate the username with the database again\n            $user = $this->auth_model->get_user_by_username($username);\n\n            if ($user) {\n                $user_data = array(\n                    'username' => $username,\n                    'logged_in' => TRUE\n                );\n                $this->session->set_userdata($user_data);\n                redirect('dashboard');\n            }\n        }\n    }\n}