function mixStrings($string1, $string2) {\n    $mixedString = '';\n    $length1 = strlen($string1);\n    $length2 = strlen($string2);\n    \n    // Find the maximum length of the two strings\n    $maxLength = max($length1, $length2);\n    \n    for ($i = 0; $i < $maxLength; $i++) {\n        if ($i < $length1) {\n            $mixedString .= $string1[$i];\n        }\n        if ($i < $length2) {\n            $mixedString .= $string2[$i];\n        }\n    }\n    \n    return $mixedString;\n}\n\n$string1 = '9cb5jplgvsiedji9mi9o6a8qq1';\n$string2 = '5d41402abc4b2a76b9719d911017c592';\n\n$result = mixStrings($string1, $string2);\necho $result;