<?php\n\nclass IdObfuscator {\n    private $secretKey;\n\n    public function __construct($secretKey) {\n        $this->secretKey = $secretKey;\n    }\n\n    public function obfuscate($id) {\n        return $id ^ $this->secretKey;\n    }\n\n    public function deobfuscate($obfuscatedId) {\n        return $obfuscatedId ^ $this->secretKey;\n    }\n\n    public function toBase36($number) {\n        return base_convert($number, 10, 36);\n    }\n\n    public function fromBase36($base36) {\n        return base_convert($base36, 36, 10);\n    }\n}\n\n// Example usage\n$secretKey = 12345; // This should be a secret value\n$obfuscator = new IdObfuscator($secretKey);\n\n$internalId = 1234;\n$obfuscatedId = $obfuscator->obfuscate($internalId);\n$base36Id = $obfuscator->toBase36($obfuscatedId);\n\necho "Internal ID: $internalId\n";\necho "Obfuscated ID: $obfuscatedId\n";\necho "Base36 ID: $base36Id\n";\n\n// To reverse the process\n$decodedBase36Id = $obfuscator->fromBase36($base36Id);\n$originalId = $obfuscator->deobfuscate($decodedBase36Id);\n\necho "Decoded Base36 ID: $decodedBase36Id\n";\necho "Original ID: $originalId\n";\n\n?>