<?php\ntry {\n    $pdo = new PDO('mysql:host=localhost;dbname=testdb', 'username', 'password');\n    $stmt = $pdo->prepare('SELECT * FROM my_table', array(\n        PDO::ATTR_CURSOR => PDO::CURSOR_SCROLL\n    ));\n    $stmt->execute();\n\n    // Fetch the first row\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_FIRST);\n    print_r($row);\n\n    // Fetch the last row\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_LAST);\n    print_r($row);\n\n    // Fetch the third row\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_ABS, 2);\n    print_r($row);\n\n    // Fetch the previous row\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_PRIOR);\n    print_r($row);\n\n    // Fetch the next row\n    $row = $stmt->fetch(PDO::FETCH_ASSOC, PDO::FETCH_ORI_NEXT);\n    print_r($row);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>